using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.IO;

namespace DiskWriter
{
    class Program
    {
        static void Main(string[] args)
        {
            bool validInput = false;
            string location = "C:/files";
            Console.WriteLine("Type in desired directory of created files!");
            while (!validInput)
            {
                location = Console.ReadLine();
                try
                {
                    Directory.CreateDirectory(location);
                    validInput = true;
                } catch
                {
                    Console.WriteLine("Drive not found, try again!");
                }
            }
            int i = 1;
            Console.WriteLine("Type in desired size of created files in KB. Please note that the values will be rounded to the next 4K unit.");
            string size = Console.ReadLine();
            while (true)
            {
                for (int j = 0; j < (Int32.Parse(size) + 3) / 4; j++)
                {
                    File.AppendAllText(location + "/file" + i, "");
                }
                i++;
            }

        }
    }
}